from util.logger import get_loggerfrom pprint import pprintlogger = get_logger()import json# ============================ Api Automatioon ============= ## ======== request module is used to make a http connection ====== #import requestsrs = requests.get("http://localhost:5000/todo/api/v1.0/tasks")logger.info(rs.status_code)pprint(rs.json())rs = requests.get("http://localhost:5000/todo/api/v1.0/tasks/1")logger.info(rs.status_code)#logger.info(rs.json())pprint(rs.json())task = {    'title': 'Learn Api Automation',    'description': 'Need to find a good Python WebFramework Tutorial',    'done': False}rs = requests.post("http://localhost:5000/todo/api/v1.0/tasks",json=task)logger.info(rs.status_code)pprint(rs.json())# 200, 201,# 300# 404 NOT FOUND# 500 SERVER ERROR# 504## ============= WebUI Automation ================== #from selenium import webdriverfrom selenium.webdriver.common.keys import Keysdriver = webdriver.Chrome("chromedriver.exe")driver.get("http://localhost:5000")driver.implicitly_wait(60)import timeelm = driver.find_element_by_name("task_id")elm.click()time.sleep(2)elm.clear()elm.send_keys(1)time.sleep(2)but_elm = driver.find_element_by_id("get_task")but_elm.click()time.sleep(2)driver.close()